require 'pry'
class Keypad
  def initialize(keypad, keys)
    @keypad = keypad
    @keys = keys
    @row_position = 1
    @column_position = 1
    @current_key = set_current_key
    @code = ""
  end

  def decode
    @keys.each do |k|
      k.split("").each do |key|
        if key == "U"
          @row_position -= 1
            if @row_position < 0
              @row_position = 0
            end
        elsif key == "L"
          @column_position -= 1
          if @column_position < 0
            @column_position = 0
          end
        elsif key == "R"
          @column_position += 1
          if @column_position > 2
            @column_position = 2
          end
        elsif key == "D"
          @row_position += 1
          if @row_position > 2
            @row_position = 2
          end
        end
        set_current_key
      end
      @code << @current_key.to_s
    end
    puts @code
  end

  def set_current_key
    @current_key = @keypad[@row_position][@column_position]
  end
end

advent = Keypad.new([[1, 2, 3], [4, 5, 6], [7, 8, 9]], ["", "", "", "", ""])
advent.decode
